// ArticleDetailPage.js
import React from 'react';
import { useParams } from 'react-router-dom';

const ArticleDetailPage = ({ articles }) => {
  const { id } = useParams();
  const article = articles.flat().find((item, index) => index === parseInt(id));

  return (
    <div>
      {article ? (
        <div>
          <h1>{article.title}</h1>
          <p>{article.content}</p>
          <p>{article.pubDate}</p>
        </div>
      ) : (
        <p>Bài báo không tồn tại</p>
      )}
    </div>
  );
};

export default ArticleDetailPage;
