import React, { useState } from 'react';
import axios from 'axios';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Navbar from './components/Navbar/Navbar';
import ArticleList from './components/ArticleList/ArticleList';
import Footer from './components/Footer/Footer';

const feedURLs = [
  { title: 'Trang chủ', url: 'https://tuoitre.vn/rss/tin-moi-nhat.rss' },
  { title: 'Thế giới', url: 'https://tuoitre.vn/rss/the-gioi.rss' },
  { title: 'Kinh doanh', url: 'https://tuoitre.vn/rss/kinh-doanh.rss' },
  { title: 'Xe', url: 'https://tuoitre.vn/rss/xe.rss' },
  { title: 'Văn Hóa', url: 'https://tuoitre.vn/rss/van-hoa.rss' },
  { title: 'Thể thao', url: 'https://tuoitre.vn/rss/the-thao.rss' },
  { title: 'Khoa học', url: 'https://tuoitre.vn/rss/khoa-hoc.rss' },
];

function App() {
  const [articles, setArticles] = useState([]);
  const [title, setTitle] = useState('');

  const getArticles = async (url, title) => {
    try {
      const res = await axios.get(`http://localhost:4000/feed?url=${encodeURIComponent(url)}`);
      setArticles(res.data);
      setTitle(title);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <BrowserRouter>
        <nav>
          {feedURLs.map(feed => (
            <Navbar key={feed.url} title={feed.title} url={feed.url} onClick={getArticles} />
          ))}
        </nav>
        <Routes>
          <Route path='/' element={<ArticleList title={title} articles={articles} />} />
        </Routes>
      </BrowserRouter>
      <Footer />
    </div>
  );
}

export default App;
