import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import './Navbar.css';
import $ from 'jquery';

const Navbar = ({ feedURLs, setSelectedCategory }) => {
  const navigate = useNavigate();
  const [activeNavLink, setActiveNavLink] = useState('');

  const handleNavLinkClick = (title) => {
    setSelectedCategory(title);
    navigate(`/${title}`);
    setActiveNavLink(`/${title}`); // Cập nhật NavLink đang được chọn
  };

  const handleScroll = () => {
    if ($(window).scrollTop() > 100) {
      $(".navbar").addClass('active');
    } else {
      $(".navbar").removeClass('active');
    }
  };

  useEffect(() => {
    // Xóa class active của tất cả các NavLink trước khi cập nhật
    $('.navbar .cate-name').removeClass('active');
    
    // Thêm class active cho NavLink hiện tại được chọn
    $(`.navbar .cate-name[to="${activeNavLink}"]`).addClass('active');
  }, [activeNavLink]);

  $(window).on('scroll', handleScroll);

  let newsList = (
    <nav className='navbar justify-content-center'>
      <ul className='d-flex m-0'>
        {feedURLs.map(feed => (
          <li className='list-unstyled mx-2' key={feed.url}>
            <NavLink
              to={`/${feed.title}`}
              className={`cate-name w-25`}
              onClick={() => handleNavLinkClick(feed.title)}
            >
              {feed.title}
            </NavLink>
          </li>
        ))}
      </ul>
    </nav>
  );

  return (
    <div className='fluid-container'>
      {newsList}
    </div>
  );
};

export default Navbar;
