import React, { useState, useEffect } from 'react';
import { NavLink, useNavigate, useLocation } from 'react-router-dom';
import classes from './Navbar.css';
import $ from 'jquery'

const Navbar = ({ feedURLs, setSelectedCategory }) => {
  const location = useLocation();
  const [activeNavLink, setActiveNavLink] = useState('');
  const navigate = useNavigate();
  
  useEffect(() => {
    handleNavLinkActive(location.pathname);
  }, [location]);

  const handleNavLinkClick = (title) => {
    setSelectedCategory(title);
    navigate(`/${title}`);
  };

  const handleNavLinkActive = (path) => {
    // Remove active class from all nav links
    $('.navbar .cate-name').removeClass('active');
    
    // Find the nav link corresponding to the current path and add active class
    $(`.navbar .cate-name[to="${path}"]`).addClass('active');
  };

  const handleScroll = () => {
    if ($(window).scrollTop() > 100) {
      $(".navbar").addClass('active');
    } else {
      $(".navbar").removeClass('active');
    }
  };

  $(window).on('scroll', handleScroll);

  let newsList = (
    <nav className='navbar justify-content-center'>
      <ul className={`d-flex m-0`}>
        {feedURLs.map(feed => (
          <li className={`list-unstyled mx-2`} key={feed.url}>
            <NavLink
              to={`/${feed.title}`}
              className={`cate-name w-25`}
              onClick={() => handleNavLinkClick(feed.title)}
            >
              {feed.title}
            </NavLink>
          </li>
        ))}
      </ul>
    </nav>
  );

  return (
    <div className='fluid-container nav-darkmode'>
      {newsList}
    </div>
  );
};

export default Navbar;
