// components/DetailPage/DetailPage.js

import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import parse from 'html-react-parser';
import './DetailPage.css';
// @ts-ignore
import 'bootstrap/dist/css/bootstrap.min.css';
import { JSDOM } from 'jsdom';


const DetailPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const query = new URLSearchParams(location.search);
  const url = query.get('url');
  const [article, setArticle] = useState({ title: '', body: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/article?url=${encodeURIComponent(url)}`);
        setArticle(response.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    if (url) {
      fetchArticle();
    }
  }, [url]);


  const handleLinkClick = async (e) => {
    if (e.target.tagName === 'A') {
      e.preventDefault();
      const newUrl = e.target.href;
      console.log("Navigating to:", newUrl);
      try {
        const response = await axios.get(`http://localhost:4000/api/article?url=${encodeURIComponent(newUrl)}`);
        const html = response.data.body; // Lấy nội dung HTML từ response
        
        // Sử dụng JSDOM để parse HTML
        const dom = new JSDOM(html);
        const document = dom.window.document;
        const title = document.querySelector('h1')?.textContent || '';
        const body = document.body.innerHTML;
        
        setArticle({ title, body });
        navigate(`/article?url=${encodeURIComponent(newUrl)}`);
      } catch (err) {
        console.error('Error fetching new article:', err);
      }
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="detail-page" onClick={handleLinkClick}>
      <h1>{article.title}</h1>
      <div  dangerouslySetInnerHTML={{ __html: article.body }}></div>
    </div>
  );
};

export default DetailPage;
