import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import CategoryPage from './components/CategoryPage/CategoryPage';
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import ArticleDetail from './components/DetailPage/DetailPage';
import './css/App.css';

const feedURLs = [
  "https://tuoitre.vn/rss/tin-moi-nhat.rss",
  "https://tuoitre.vn/rss/the-gioi.rss",
  "https://tuoitre.vn/rss/kinh-doanh.rss",
  "https://tuoitre.vn/rss/xe.rss",
  "https://tuoitre.vn/rss/van-hoa.rss",
  "https://tuoitre.vn/rss/the-thao.rss",
  "https://tuoitre.vn/rss/khoa-hoc.rss",
  "https://tuoitre.vn/rss/gia-that.rss",
  "https://tuoitre.vn/rss/ban-doc-lam-bao.rss",
  "https://tuoitre.vn/rss/thoi-su.rss",
  "https://tuoitre.vn/rss/phap-luat.rss",
  "https://tuoitre.vn/rss/cong-nghe.rss",
  "https://tuoitre.vn/rss/nhip-song-tre.rss",
  "https://tuoitre.vn/rss/giai-tri.rss",
  "https://tuoitre.vn/rss/giao-duc.rss",
  "https://tuoitre.vn/rss/suc-khoe.rss",
  "https://tuoitre.vn/rss/thu-gian.rss",
  "https://tuoitre.vn/rss/du-lich.rss",
];

const App = () => {
  const [articles, setArticles] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(feedURLs[0].url);

  useEffect(() => {
    const fetchArticles = async () => {
      const response = await axios.get('http://localhost:4000/api/rss');
      setArticles(response.data);
    };

    fetchArticles();
  }, []);

  const filteredArticles = articles.filter(article => article.category === selectedCategory);

  return (
    <Router>
      <Header />
      <Navbar feedURLs={feedURLs} setSelectedCategory={setSelectedCategory} />
      <Routes>
        <Route path="/home/:category" element={<CategoryPage articles={filteredArticles} />} />
        <Route path="/article" element={<ArticleDetail />} />
        <Route path="*" element={<Navigate to={`/home/${feedURLs[0].title}`} replace />} />
      </Routes>
      <Footer />
    </Router>
  );
};

export default App;
