import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Navbar from './components/Navbar/';
import ArticleList from './components/ArticleList';
import Footer from './components/Footer';

const feedURLs = [
  { title: 'Trang chủ', url: 'https://tuoitre.vn/rss/tin-moi-nhat.rss' },
  { title: 'Thế giới', url: 'https://tuoitre.vn/rss/the-gioi.rss' },
  { title: 'Kinh doanh', url: 'https://tuoitre.vn/rss/kinh-doanh.rss' },
  { title: 'Xe', url: 'https://tuoitre.vn/rss/xe.rss' },
  { title: 'Văn Hóa', url: 'https://tuoitre.vn/rss/van-hoa.rss' },
  { title: 'Thể thao', url: 'https://tuoitre.vn/rss/the-thao.rss' },
  { title: 'Khoa học', url: 'https://tuoitre.vn/rss/khoa-hoc.rss' },
];

function App() {
  const [articles, setArticles] = useState({});

  const getArticles = async () => {
    try {
      const res = await axios.get('http://localhost:4000/');
      const groupedArticles = res.data.reduce((acc, article) => {
        const feedURL = feedURLs.find(feed => article.category === feed.url);
        if (feedURL) {
          if (!acc[feedURL.title]) {
            acc[feedURL.title] = [];
          }
          acc[feedURL.title].push(article);
        }
        return acc;
      }, {});
      setArticles(groupedArticles);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getArticles();
  }, []);

  return (
    <div>
      <BrowserRouter>
        <nav>
          {feedURLs.map(feed => (
            <Navbar key={feed.url} title={feed.title} url={feed.url} />
          ))}
        </nav>
        <Routes>
          {feedURLs.map(feed => (
            <Route
              key={feed.url}
              path={`/feed/${encodeURIComponent(feed.url)}`}
              element={<ArticleList title={feed.title} articles={articles[feed.title] || []} />}
            />
          ))}
        </Routes>
      </BrowserRouter>
      <Footer />
    </div>
  );
}

export default App;
