import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Thư viện để đọc rss
import cheerio from 'cheerio'; // Thư viện xử lý rss
import styles from '../detail/Detail.module.css';
import { useParams } from 'react-router-dom';

const Detail = ({ articles }) => {
    const { link } = useParams(); // Lấy tham số từ URL, trong trường hợp này là link
    const [detail, setDetail] = useState(null); // State để lưu chi tiết bài viết
    const [feedItems, setFeedItems] = useState([]); // State để lưu danh sách các bài viết liên quan

    // Hàm giải mã các ký tự đặc biệt trong chuỗi HTML
    function decodeHTMLEntities(text) {
        const entities = {
            '&apos;': "'",
            '&amp;': '&',
            '&lt;': '<',
            '&gt;': '>',
            '&quot;': '"',
        };

        // Thay thế các ký tự đặc biệt trong chuỗi với các thực thể tương ứng
        Object.keys(entities).forEach(entity => {
            const regex = new RegExp(entity, 'g');
            text = text.replace(regex, entities[entity]);
        });

        // Sử dụng DOMParser để phân tích chuỗi HTML và trả về nội dung văn bản của nó
        const parser = new DOMParser();
        const doc = parser.parseFromString(text, 'text/html');
        return doc.documentElement.textContent || text;
    }

    // Hàm trích xuất đường dẫn từ URL
    const extractLinkPath = (url) => {
        const parts = url.split('/');
        return parts[parts.length - 1];
    };

    // Sử dụng useEffect để gọi API và cập nhật state khi tham số link thay đổi
    useEffect(() => {
        async function fetch() {
            try {
                // Lấy HTML từ articles.content để xử lý
                const html = articles.content;
                const $ = cheerio.load(html);

                // Xử lý các lớp CSS trong HTML để sử dụng className thay vì class
                $('[class]').each((index, element) => {
                    const classes = $(element).attr('class')?.split(' ') || [];
                    classes.forEach(className => {
                        $(element).removeClass(className).addClass(className);
                    });
                });

                // Trích xuất các thông tin cần thiết từ HTML
                const title = decodeHTMLEntities(articles.title);
                const content = $.html();
                const dateUp = articles.isoDate;
                const demo = decodeHTMLEntities(articles.contentSnippet);
                const navItemsFiltered = []; // Không có thông tin breadcrumb trong props articles
                
                // Cập nhật state detail với các thông tin vừa trích xuất được
                setDetail({ title, demo, content, dateUp, navItems: navItemsFiltered });

            } catch (error) {
                console.error('Error fetching the HTML:', error);
            }
        }

        // Gọi hàm fetch khi component mount
        fetch();
    }, []); // useEffect sẽ chỉ gọi một lần khi component mount

    // Render UI
    return (
        <div className="container">
            <div className={"subContainer"}>
                <div className="breadCrumbDetail">
                    <ul>
                        {/* Không có thông tin breadcrumb trong props articles */}
                    </ul>
                    {/* <div>{detail?.dateUp}</div> */}
                </div>
                <div>
                    <h1 className={styles.contentDetailTitle}>{detail?.title || 'Loading...'}</h1>
                    <h2 className={styles.contentDetailSapo}>{detail?.demo}</h2>
                    <div className={styles.maincontent}
                         dangerouslySetInnerHTML={{ __html: detail?.content || '' }}>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Detail;
