// App.js

import React, { useState } from 'react';
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import Navbar from './components/Navbar/Navbar';
import CategoryPage from './components/CategoryPage/CategoryPage';
import axios from 'axios'; // Bạn có thể sử dụng axios để gửi yêu cầu HTTP từ client
import Header from './components/Header/Header';
import ArticleCard from './components/Card/Card';
import Footer from './components/Footer/Footer';
import ArticleDetailPage from './components/ArticleDetailPage/ArticleDetailPage';

const feedURLs = [
  { title: 'Trang chủ', url: 'https://tuoitre.vn/rss/tin-moi-nhat.rss' },
  { title: 'Thế giới', url: 'https://tuoitre.vn/rss/the-gioi.rss' },
  // Thêm các URL RSS feeds khác nếu cần
];

function App() {
  const [selectedCategory, setSelectedCategory] = useState('home');
  const [articles, setArticles] = useState([]);

  // Hàm fetch danh sách bài viết từ server
  const fetchArticles = async () => {
    try {
      const response = await axios.get('http://localhost:4000/');
      setArticles(response.data);
    } catch (error) {
      console.error('Error fetching articles:', error);
    }
  };

  // Sử dụng useEffect để fetch dữ liệu khi component được mount
  useEffect(() => {
    fetchArticles();
  }, []);

  return (
    <div>
      <Header />
      <BrowserRouter>
        <Navbar feedURLs={feedURLs} setSelectedCategory={setSelectedCategory} />
        <Routes>
          <Route path="/home/:category" element={<CategoryPage articles={articles[selectedCategory] || []} />} />
          <Route path="/article-detail/:articleId" element={<ArticleDetailPage />} />
          <Route path="*" element={<Navigate to="/home/Trang chủ" replace />} />
        </Routes>
      </BrowserRouter>
      <Footer />
    </div>
  );
}

export default App;
