import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from './components/Navbar/Navbar';
import { BrowserRouter, Route, Routes, Link } from "react-router-dom";

import Feed from './components/Feed';

const feedURLs = [
  { title: 'Trang chủ', url: 'https://tuoitre.vn/rss/tin-moi-nhat.rss' },
  { title: 'Thế giới', url: 'https://tuoitre.vn/rss/the-gioi.rss' },
  { title: 'Kinh doanh', url: 'https://tuoitre.vn/rss/kinh-doanh.rss' },
  { title: 'Xe', url: 'https://tuoitre.vn/rss/xe.rss' },
  { title: 'Văn Hóa', url: 'https://tuoitre.vn/rss/van-hoa.rss' },
  { title: 'Thể thao', url: 'https://tuoitre.vn/rss/the-thao.rss' },
  { title: 'Khoa học', url: 'https://tuoitre.vn/rss/khoa-hoc.rss' },
];

function App() {
  const [articles, setArticles] = useState({});
  const [selectedCategory, setSelectedCategory] = useState('Trang chủ');

  const getArticles = async () => {
    try {
      const res = await axios.get('http://localhost:4000/');
      const groupedArticles = res.data.reduce((acc, article) => {
        const feedURL = feedURLs.find(feed => article.category === feed.url);
        if (feedURL) {
          if (!acc[feedURL.title]) {
            acc[feedURL.title] = [];
          }
          acc[feedURL.title].push(article);
        }
        return acc;
      }, {});
      setArticles(groupedArticles);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getArticles();
  }, []);

  return (
    <div>
      <BrowserRouter>
        <Navbar feedURLs={feedURLs} setSelectedCategory={setSelectedCategory} />
        <Routes>
          <Route path="home" element={<CategoryPage articles={articles[selectedCategory] || []} />} />
        </Routes>
        <Link index to="home">Home</Link>
      </BrowserRouter>
    </div>
  );
}

const CategoryPage = ({ articles }) => {
  return (
    <div>
      {articles.map((item, i) => (
        <Feed
          key={i}
          content={item.content}
          contentSnippet={item.contentSnippet}
          title={item.title}
          link={item.link}
          date={item.pubDate}
        />
      ))}
    </div>
  );
};

export default App;
