import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import ArticleCard from '../Card/Card';
import axios from 'axios';
// import './SearchArticle.css';

const removeAccents = (str) => {
  return str.normalize('NFD').replace(/[\u0300-\u036f]/g, '').replace(/đ/g, 'd').replace(/Đ/g, 'D');
};

const SearchArticle = () => {
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const keyword = query.get('keyword');
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchSearchResults = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:4000/search/${encodeURIComponent(keyword)}`);
        setSearchResults(response.data);
        setLoading(false);
      } catch (err) {
        setError(`Error fetching search results: ${err.message}`);
        setLoading(false);
      }
    };

    if (keyword) {
      fetchSearchResults();
    }
  }, [keyword]);


  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="search-results">
      <h2>Kết quả tìm kiếm cho: "{decodeURIComponent(keyword)}"</h2>
      {searchResults.length === 0 ? (
        <p>Không có kết quả tìm kiếm, vui lòng thử lại.</p>
      ) : (
        searchResults.map((article, index) => (
          <ArticleCard
            key={index}
            title={article.title}
            contentSnippet={article.contentSnippet}
            link={article.link}
            content={article.content}
          />
        ))
      )}
    </div>
  );
};

export default SearchArticle;
