import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './DetailPage.css';

const DetailPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const query = new URLSearchParams(location.search);
  const initialUrl = query.get('url');
  const [url, setUrl] = useState(initialUrl);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticle = async () => {
      if (!url) return;

      try {
        const response = await axios.get(`http://localhost:4000/api/article?url=${encodeURIComponent(url)}`);
        if (response.status === 200) {
          const articleData = response.data;
          // Lưu dữ liệu vào localStorage để tab mới có thể lấy
          localStorage.setItem('articleData', JSON.stringify(articleData));
          setLoading(false);
        } else {
          setError('Không tìm thấy nội dung bài báo.');
          setLoading(false);
        }
      } catch (err) {
        setError(`Lỗi khi tải bài báo: ${err.message}`);
        setLoading(false);
      }
    };

    fetchArticle();
  }, [url]);

  const handleLinkClick = (e) => {
    e.preventDefault();
    if (e.target.tagName === 'A' && e.target.href) {
      const newUrl = new URL(e.target.href);
      window.open(e.target.href, '_blank'); // Mở liên kết trong tab mới
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="detail-page" onClick={handleLinkClick}>
      {/* Hiển thị dữ liệu bài báo */}
      <h1>{localStorage.getItem('articleData') && JSON.parse(localStorage.getItem('articleData')).title}</h1>
      <div dangerouslySetInnerHTML={{ __html: localStorage.getItem('articleData') && JSON.parse(localStorage.getItem('articleData')).body }}></div>
    </div>
  );
};

export default DetailPage;
