import React from 'react';
import { useParams } from 'react-router-dom';

const ArticleDetailPage = ({ articles }) => {
    // Kiểm tra nếu articles không phải là mảng thì gán là một mảng rỗng
    const articleList = Array.isArray(articles) ? articles : [];
  
    // Sử dụng useParams() để lấy id từ URL
    const { id } = useParams();
  
    // Tìm bài báo với id tương ứng trong danh sách articles
    const article = articleList.find(item => item.id.toString() === id);
  
    if (!article) {
      return <p>Bài báo không tồn tại</p>;
    }
  
    // Hiển thị nội dung bài báo
    return (
      <div>
        <h1>{article.title}</h1>
        <p>{article.content}</p>
        <p>{article.pubDate}</p>
      </div>
    );
  };

export default ArticleDetailPage;
