import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import Header from './components/Header/Header';
import Navbar from './components/Navbar/Navbar';
import CategoryPage from './components/CategoryPage/CategoryPage';
import Footer from './components/Footer/Footer';
import ArticleDetail from './components/ArticleDetail/ArticleDetail';
import './App.css'; // Đảm bảo css của bạn được import

const App = () => {
  return (
    <div className="App">
      <Header />
      <Router>
        <Navbar feedURLs={feedURLs} /> {/* Feed URLs sẽ được chuyển từ đây */}
        <Routes>
          {/* Route cho từng category */}
          {feedURLs.map((feed, index) => (
            <Route
              key={index}
              path={`/category/${index}`}
              element={<CategoryPage feed={feed} />}
            />
          ))}
          <Route path="/article" element={<ArticleDetail />} />
          {/* Route mặc định chuyển hướng đến Trang chủ */}
          <Route path="*" element={<Navigate to="/category/0" replace />} />
        </Routes>
      </Router>
      <Footer />
    </div>
  );
};

export default App;
