// components/DetailPage/DetailPage.js

import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import parse from 'html-react-parser';
import './DetailPage.css';
// @ts-ignore
import 'bootstrap/dist/css/bootstrap.min.css';

const DetailPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const query = new URLSearchParams(location.search);
  const url = query.get('url');
  const [article, setArticle] = useState({ title: '', body: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/article?url=${url}`);
        setArticle(response.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchArticle();
  }, [url]);


  const handleLinkClick = (e) => {
    if (e.target.tagName === 'a') {
      e.preventDefault();
      const newUrl = e.target.href;
      navigate(`/article?url=${encodeURIComponent(newUrl)}`);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="detail-page" onClick={handleLinkClick}>
      <h1>{article.title}</h1>
      <div dangerouslySetInnerHTML={{ __html: article.body }}></div>
    </div>
  );
};

export default DetailPage;
