import React from 'react';
import axios from 'axios'
import { useEffect, useState } from 'react';
import Feed from './Feed';

function App() {
  const [articles, setArticles] = useState([]);

  console.log(articles);

  const getArticles = async () => {
    try {
      const res = await axios.get('http://localhost:4000/');
      setArticles(res.data)
    } catch (error) {

    }
  }

  useEffect(() => {
    getArticles();
  }, [])

  const escapeHtml = (html) => {
    return html
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/"/g, '&quot;')
      .replace(/'/g, '&#039;');
  };

  console.log(articles);
  return (
    <div>
      <h1 className={`head`}>Rss feed</h1>
      <h2>Goodmorning TAT</h2>
      {articles.map((item, i) =>
        <Feed
          key={i}
          content={escapeHtml(item.item.content)}
          contentSnippet={escapeHtml(item.item.contentSnippet)}
          title={escapeHtml(item.item.title)}
          link={escapeHtml(item.item.link)}
          date={item.item.pubDate}
        />
      )}
    </div>
  );
};

export default App;
