import React from 'react';
import { useParams } from 'react-router-dom';
import Card from '../Card/Card';

const ArticleDetailPage = ({ articles }) => {
  const { index } = useParams(); // Lấy chỉ số từ URL

  const articleIndex = parseInt(index, 10); // Chuyển đổi index từ string sang số nguyên
  const article = articles[articleIndex];

  if (!article) {
    return <p>Bài báo không tồn tại</p>;
  }

  return (
    <div>
      <h1>{article.title}</h1>
      <p>{article.content}</p>
      <p>{article.pubDate}</p>
    </div>
  );
};

export default ArticleDetailPage;
